import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSort, faSortUp, faSortDown } from '@fortawesome/free-solid-svg-icons';
import PropTypes from 'prop-types';


function Ordenacao(props){
    
    function handleAscDesc(){
        return (props.ordernarAsc || props.ordernarDesc) ? 'hidden' : '';
    }

    function handleAsc(){
        return props.ordernarAsc  ? '' : 'hidden' ;
    }
    
    function handleDesc(){
        return props.ordernarDesc  ? '' : 'hidden' ;
    }

    return(

        <span>

            <FontAwesomeIcon icon={faSort}
                className={handleAscDesc()}
                data-testid="faSort" />

            <FontAwesomeIcon icon={faSortUp}
                className={handleAsc()}
                data-testid="faSortUp" />


            <FontAwesomeIcon icon={faSortDown}
                className={handleDesc()}
                data-testid="faSortDown" />

        </span>

    );

    
}

Ordenacao.propTypes = {
    ordernarAsc: PropTypes.bool.isRequired,
    ordernarDesc: PropTypes.bool.isRequired
}

export default Ordenacao;
